    render() {
        return (
            <div className="list-group">
                 <a className="list-group-item">
                    <div className="comment-avatar">
                        <Img src={"/avatar/" + '.jpg'} alt=""/>
                    </div>
                    <div className="username">
                        <h4>item.username</h4> 
                        <p>item.datetime</p>
                    </div>
                    <div className="content">
                        <p>item.content</p>
                    </div>                         
                </a>
            </div>
        )
    }
}
