import React from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom';
import { url } from '../url'
import { connect } from 'react-redux';
import { login } from '../actions'
axios.defaults.withCredentials = true;

class SignIn extends React.Component{

    state = {
        username: '',
        password:'',
        error: null,
        isloading: false
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    handleClick = () => {
        this.props.history.push('/signup');
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const user = {
            username: this.state.username,
            password: this.state.password
        }
        // console.log(user);
        // axios.post(url + '/checklogin',user).then(res => {
        //     //console.log(res.data);
        //     this.setState({
        //         code: res.data.code
        //     })    
        // });

        this.setState({isloading: true});
        this.props.login(user).then(res => {
            this.setState({
                isloading: false,
                error: res.data.error
            })
        })
    }

    render(){
        const error = this.state.error;
        const username = this.props.auth.user;
        if(username){
          window.location = '/'
        } 
        return (
            <div className="signin-container">
                <div className="signin-panel">
                    <p>Log in to Blog</p>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <input type="text" 
                                className="form-control" 
                                id="username" 
                                onChange={this.handleChange}
                                placeholder="Username" />
                        </div>
                        <div className="form-group">
                            <input type="password" 
                                className="form-control" 
                                id="password" 
                                onChange={this.handleChange}
                                placeholder="Password" />
                        </div>
                        {
                            this.state.error &
                            <div className="alert alert-danger" role="alert">{this.state.error}</div>
                        }


                        <button type="submit" className="signin-btn" disabled={this.state.isloading}>Log In</button>
                    </form>
                    <div className="con">
                        <i></i>
                        <p>or</p>
                    </div>
                    <button className="create_account" onClick={this.handleClick}>Create a new account</button>
                </div>



            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        auth: state.auth
    }
}

export default connect(mapStateToProps, { login })(SignIn);
