import React from 'react'
import axios from 'axios'
import MyPostList from '../components/MyPostList'
import Loader from 'react-loader-spinner';
import { url } from '../url'
import { Redirect } from 'react-router-dom'

export default class MyPost extends React.Component {

    state = {
        username: '',
        sessioncode: '',
        avatar: '',
        loading: false,
        redirect: false
    }

    componentDidMount(){
        this.setState({loading: true}, () => {
            axios.get(url + '/getsession').then(res => {
                this.setState({
                    loading: false,
                    username: res.data.username,
                    sessioncode: res.data.code,
                    avatar: res.data.avatar
                })
           }).then(() => this.setState({
               redirect: this.state.username? false: true
           }))
        })  
    }


    render(){

        // if (!this.state.redirect){
        //     console.log(this.state);
        //     return <Redirect to="/signin" />
        // }
        return (
            <div className="mypost-container">
                {
                    this.state.loading?
                    <Loader style={{"display": "flex", "justify-content": "center", "margin": "auto"}}
                            type="ThreeDots" 
                            color="#e87110" 
                            height="100" 
                            width="100" />
                    : (
                        !this.state.redirect ? 
                        <MyPostList />
                        :
                        <Redirect to='/signin' />
                        // <p>log in please</p>
                    )
                }
            </div>
        )
    }
}
