import React from 'react'
import Axios from 'axios';
import { url } from '../url'
import Img from '../components/Img'


class PostDetail extends  React.Component {

    state = {
       post: []
    }

    componentDidMount(){
        Axios.get(url + '/getpostwithid?pid=' + this.props.match.params.pid).then(res => {
            // console.log(res.data.result)
            this.setState({
                post: res.data.result
            })
        })
    }

    render(){
        console.log(this.state.post)
        return ( 
            <div className="post-container">
               {
                   this.state.post.map((item, index) => {
                       return (
                        <a className="list-group-item" key={this.props.index}>
                            <div style={{"display": "flex", "flexDirection": "row"}}>
                                <div className="comment-avatar">
                                    <Img src={url + "/avatar/" + this.props.item.username + '.jpg'} alt=""/>
                                </div>
                                 <div className="username">
                                    <h4>{this.props.item.username}</h4> 
                                    <p>{this.props.item.datetime}</p>
                                </div>
                             </div>
                            <div className="content">
                                <p>{this.props.item.content}</p>
                            </div>

        </a>
                       )
                   })
               }

           </div>

        )
    }
}



export default PostDetail
